#!/bin/bash
curl -H "Authorization: Basic %base64encode_user:passwd%" --request POST -H "Content-Type: application/json" https://helpdesk.dom.gosuslugi.ru/rest/api/2/search --data '{"jql": "project = SUPP AND issuetype != Sub-task AND status changed during (\"2022-12-01 00:00\", \"2023-01-01 00:00\") to Закрыто  AND filter = без_метки_тест ORDER BY created ASC", "maxResults":"20000", "fields":["id","key", "created"], "expand":["changelog"]}' | jq . >> jira_raw.json;
